[{"text": "#!/bin/bash\n\n# Quick start script for local development and testing\n\nset -e\n\necho \"=====================================\"\necho \"Snowflake Notebooks Migration Guide\"\necho \"Quick Start Script\"\necho \"=====================================\"\necho \"\"\n\n# Check if Docker is installed\nif ! command -v docker &> /dev/null; then\n    echo \"Error: Docker is not installed. Please install Docker first.\"\n    exit 1\nfi\n\necho \"Step 1: Building Docker image...\"\ndocker build -t migration-guide:latest .\n\nif [ $? -eq 0 ]; then\n    echo \"\\u2713 Docker image built successfully\"\nelse\n    echo \"\\u2717 Docker build failed\"\n    exit 1\nfi\n\necho \"\"\necho \"Step 2: Starting Streamlit application...\"\necho \"The app will be available at: http://localhost:8501\"\necho \"Press Ctrl+C to stop the application\"\necho \"\"\n\nsleep 2\n\n# Run the container\ndocker run -p 8501:8501 \\\n    --name migration-guide-local \\\n    --rm \\\n    migration-guide:latest\n", "type": "text"}]